$(document).ready(function(){$("audio,video").mediaelementplayer({audioWidth:112,features:["playpause","volume"],success:function(e){e.addEventListener("loadeddata",function(){$(document).snowfall({flakeCount:150,round:!0,minSize:5,maxSize:8});$("#moon").delay(2e3).animate({top:"55px"},6e3,function(){$("#santa").animate({top:"133px",left:"35px"},4e3,function(){$("#message").delay(1e3).fadeIn(3e3)})})},!1)}})});$("#billboard-media img:gt(0)").hide();setInterval(function(){$("#billboard-media :first-child").fadeOut(100).next("img").fadeIn(100).end().appendTo("#billboard-media")},5500);